package com.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	// storing and representing numeric values	
		/*
		 * box1 is considered a variable
		 * box can be any name 
		 * just used for storing values
		 * to store something you use an = sign
		 * byte is the smallest box and boxes increase in size going up to long
		 * 
		 * 
		 * 
		 */
	byte box1 = 127;	// 127 the maximum capacity
	
	short box2 = 32767; // 32767 is maximum capacity (least useful)
	
	int box3 = 100000; // *most popular* 214784
	
	long box4 = 100000000; // biggest capacity. You need to add L or l at the end of number to specify its a long number. Are usually used for credit cards.
	
	
	
	// storing and representing decimal values; 
	float variable = 1.99F; // single numerical value
	
	double variable2 = 99.99; // double numerical value
	
	
	
	// storing single characters
	char container = 'A'; // upper case lower case does not matter. Single character single quotes''
	char dollar = '$';
	
	
	
 // storing yes or no values
	boolean hot = true;
    boolean tired = false;
    
    
	
	System.out.println(box1); // printing value of the variable
	
	System.out.println("box1"); // printing text variable
	
	
	System.out.println("");
	
	
	box1=1;
	box2=2;
	box3=3;
	box4=4;
	variable=5;
	variable2=6;
	container=7;
	dollar=8;
	hot=false;
	tired=true;
			
	
	
	System.out.println(box1);
	System.out.println(box2);
	System.out.println(box3);
	System.out.println(box4);
	System.out.println(variable);
	System.out.println(variable2);
	System.out.println(container);
	System.out.println(dollar);
	System.out.println(hot);
	System.out.println(tired);
	
	
		

	}

}
